import { defineStore } from "pinia";
import { markRaw } from "vue";
import { route } from "quasar/wrappers";

pinia.use(({ store }) => {
  // important! dont add a $router here
  store.router = markRaw(route);
});
export const useAuthStore = defineStore({
  state: () => ({
    token: "",
  }),
});
