import axios from "axios";
window.axios = axios;

axios.defaults.baseURL = "http://localhost:8000/";

axios.interceptors.response.use(
  //onFulfilled
  (response) => response,
  //onRejected
  (error) => {
    if (error.code === 401) {
      console.log("IT WORKED");
    }
    return Promise.reject(error);
  }
);

axios.interceptors.request.use(
  //onFulfilled
  (response) => response,
  //onRejected
  (error) => {
    if (error.code === 401) {
      console.log("IT WORKED");
    }
    return Promise.reject(error);
  }
);
