import { defineStore } from "pinia";
import { markRaw } from "vue";
// adapt this based on where your router is
import { router } from "./router";

pinia.use(({ store }) => {
  store.router = markRaw(router);
});
export const useAuthStore = defineStore({
  state: () => ({
    token: "",
  }),
});
